import { addCommonDirectiveOptions, commonDirectiveOptions } from './utils.js';
export const divDirective = {
    name: 'div',
    options: {
        ...commonDirectiveOptions('div'),
    },
    body: {
        type: 'myst',
        required: true,
    },
    run(data) {
        const div = {
            type: 'div',
            children: data.body,
        };
        addCommonDirectiveOptions(data, div);
        return [div];
    },
};
