import { addCommonDirectiveOptions, commonDirectiveOptions } from './utils.js';
export const glossaryDirective = {
    name: 'glossary',
    body: {
        type: 'myst',
        required: true,
    },
    options: {
        ...commonDirectiveOptions('glossary'),
    },
    run(data) {
        const glossary = {
            type: 'glossary',
            children: data.body,
        };
        addCommonDirectiveOptions(data, glossary);
        return [glossary];
    },
};
