import { addCommonDirectiveOptions, commonDirectiveOptions } from './utils.js';
export const mdastDirective = {
    name: 'mdast',
    arg: {
        type: String,
        required: true,
    },
    options: {
        ...commonDirectiveOptions('mdast'),
    },
    run(data) {
        const mdast = {
            type: 'mdast',
            id: data.arg,
        };
        addCommonDirectiveOptions(data, mdast);
        return [mdast];
    },
};
