import { addCommonDirectiveOptions, commonDirectiveOptions } from './utils.js';
export const mermaidDirective = {
    name: 'mermaid',
    options: {
        ...commonDirectiveOptions('mermaid'),
    },
    body: {
        type: String,
        required: true,
    },
    run(data) {
        return [
            addCommonDirectiveOptions(data, {
                type: 'mermaid',
                value: data.body,
            }),
        ];
    },
};
